    GesetzestextSTGBAT(buch: "",     teil: "",     titel: "",     kapitel: "",     abschnitt: "",     artikel: "Art. 62b",     marginale1: "Endgültige Entlassung",     marginale2: "",     absatz0: "",     absatz0litaz: "",     absatz0lita: "",     absatz0litaziffer1z: "",     absatz0litaziffer1: "",     absatz0litaziffer2z: "",     absatz0litaziffer2: "",     absatz0litaziffer3z: "",     absatz0litaziffer3: "",     absatz0litaziffer4z: "",     absatz0litaziffer4: "",     absatz0litbz: "",     absatz0litb: "",     absatz0litcz: "",     absatz0litc: "",     absatz0litdz: "",     absatz0litd: "",     absatz0litez: "",     absatz0lite: "",     absatz0litfz: "",     absatz0litf: "",     absatz0litgz: "",     absatz0litg: "",     absatz0lithz: "",     absatz0lith: "",     absatz1z: "1",     absatz1: "    Hat sich der bedingt Entlassene bis zum Ablauf der Probezeit bewährt, so ist er endgültig entlassen.",     marginaleabsatz1lita: "",     absatz1litaz: "",     absatz1lita: "",     absatz1litaziffer1z: "",     absatz1litaziffer1: "",     absatz1litaziffer2z: "",     absatz1litaziffer2: "",     absatz1litaziffer3z: "",     absatz1litaziffer3: "",     absatz1litabisz: "",     absatz1litabis: "",     marginaleabsatz1litb: "",     absatz1litbz: "",     absatz1litb: "",     absatz1litbziffer1z: "",     absatz1litbziffer1: "",     absatz1litbziffer2z: "",     absatz1litbziffer2: "",     absatz1litbziffer3z: "",     absatz1litbziffer3: "",     marginaleabsatz1litc: "",     absatz1litcz: "",     absatz1litc: "",     absatz1litcbisz: "",     absatz1litcbis: "",     marginaleabsatz1litd: "",     absatz1litdz: "",     absatz1litd: "",     marginaleabsatz1lite: "",     absatz1litez: "",     absatz1lite: "",     absatz1liteziffer1z: "",     absatz1liteziffer1: "",     absatz1liteziffer2z: "",     absatz1liteziffer2: "",     marginaleabsatz1litf: "",     absatz1litfz: "",     absatz1litf: "",     absatz1litfbisz: "",     absatz1litfbis: "",     marginaleabsatz1litg: "",     absatz1litgz